import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import 'tiny-slider/dist/tiny-slider.css';
import { tns } from "tiny-slider/src/tiny-slider";

const root = ReactDOM.createRoot(document.getElementById('root'));

root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

// Function to initialize tiny-slider after images are moved
const initializeSlider = () => {
  const sliderContainer = document.getElementById('slider-container');
  const images = document.querySelectorAll('#root img');

  // Move images to slider container
  images.forEach(img => sliderContainer.appendChild(img));

  // Initialize tiny-slider
  tns({
    container: '#slider-container',
    items: 1,
    slideBy: 'page',
    autoplay: true,
    controls: true,
    nav: true,
    autoplayButtonOutput: false,
  });
};

// Call initializeSlider after the component has rendered
setTimeout(initializeSlider, 0);
